mapper.select:
SELECT COUNT(*) FROM GOODS WHERE SALE_YN = 'Y'
AND CATEGORY = #{category}
AND GOODS_NAME LIKE '%' || #{keyword} || '%'

mapper.update:
UPDATE GOODS_COMMENT 
SET 
	STAR = #{star}
	, MEMO = #{memo}
WHERE 
	CUST_ID = #{cust_id}
	AND GOODS_COMMENT_SEQ = #{goods_comment_seq}

mapper.delete:
DELETE GOODS_COMMENT 
WHERE 
	CUST_ID = #{cust_id}
	AND GOODS_COMMENT_SEQ = #{goods_comment_seq}

mapper.insert:
INSERT INTO GOODS_COMMENT(
	  GOODS_COMMENT_SEQ
	, GOODS_SEQ
	, CUST_ID
	, STAR
	, MEMO
	, REGDATE
) VALUES(
	  SEQ_GOODS_COMMENT_SEQ.NEXTVAL
	, #{goods_seq}
	, #{cust_id}
	, #{star}
	, #{memo}
	, SYSDATE
)

INSERT INTO GOODS_DETAIL_IMG(
	  GOODS_SEQ
	, IMG_ORDER
	, IMG_URL
	, REGDATE
	, REGUSER
) VALUES(
	  #{goods_seq}
	, #{img_order}
	, #{img_url}
	, SYSDATE
	, #{reguser}
)

mapper.update:
UPDATE GOODS SET 
	GOODS_DETAIL = #{goods_detail}
	, EDITDATE = SYSDATE
	, EDITUSER = #{reguser} 
WHERE 
	GOODS_SEQ = #{goods_seq}
	AND COMP_SEQ = (SELECT COMP_SEQ FROM COMPANY WHERE CUST_ID = #{reguser})
